mapper.select:
SELECT p.*,ro.* from power_manager p LEFT JOIN power_manger_role_rf rf on p.manager_id=rf.rf_mangerid LEFT JOIN power_role ro on rf.rf_roleid=ro.role_id
manager_phone like CONCAT('%',#{param},'%') or manager_idcard like CONCAT('%',#{param},'%') or manager_name like CONCAT('%',#{param},'%')
and manager_phone=#{phone}
order by manager_id asc
order by ${prop} ${orderx}

